import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ItemList = ({ refresh }) => {
  const [items, setItems] = useState([]);

  useEffect(() => {
    const fetchItems = async () => {
      try {
        const config = {
          headers: {
            'x-auth-token': localStorage.getItem('token'),
          },
        };
        const res = await axios.get('/api/items', config);
        setItems(res.data);
      } catch (err) {
        console.error(err.response.data);
      }
    };
    fetchItems();
  }, [refresh]);

  const onDelete = async (id) => {
    try {
      const config = {
        headers: {
          'x-auth-token': localStorage.getItem('token'),
        },
      };
      await axios.delete(`/api/items/${id}`, config);
      setItems(items.filter(item => item._id !== id));
    } catch (err) {
      console.error(err.response.data);
    }
  };

  return (
    <div>
      <h2>Your Items</h2>
      {items.length === 0 ? (
        <p>No items found. Add a new item above!</p>
      ) : (
        <ul>
          {items.map((item) => (
            <li
              key={item._id}
              style={item.quantity <= item.minStockThreshold ? { color: 'red', fontWeight: 'bold' } : {}}
            >
              <strong>{item.name}</strong> (Store: {item.store.name}, Category: {item.category.name}) - Price: {item.store.currency}{item.price}, Quantity: {item.quantity}, Min Stock: {item.minStockThreshold}
              {item.quantity <= item.minStockThreshold && (
                <span style={{ marginLeft: '10px', color: 'red' }}>LOW STOCK!</span>
              )}
              <button onClick={() => onDelete(item._id)}>Delete</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ItemList;
