import React, { useEffect, useState } from 'react';
import axios from 'axios';

const OrderList = ({ refresh }) => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const config = {
          headers: {
            'x-auth-token': localStorage.getItem('token'),
          },
        };
        const res = await axios.get('/api/orders', config);
        setOrders(res.data);
      } catch (err) {
        console.error(err.response.data);
      }
    };
    fetchOrders();
  }, [refresh]);

  const handleStatusChange = async (orderId, newStatus) => {
    try {
      const config = {
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': localStorage.getItem('token'),
        },
      };
      await axios.put(`/api/orders/${orderId}`, { status: newStatus }, config);
      // Refresh the list to show updated status and potentially updated item quantities
      const res = await axios.get('/api/orders', config);
      setOrders(res.data);
    } catch (err) {
      console.error(err.response.data);
    }
  };

  const onDelete = async (id) => {
    try {
      const config = {
        headers: {
          'x-auth-token': localStorage.getItem('token'),
        },
      };
      await axios.delete(`/api/orders/${id}`, config);
      setOrders(orders.filter(order => order._id !== id));
    } catch (err) {
      console.error(err.response.data);
    }
  };

  return (
    <div>
      <h2>Your Orders</h2>
      {orders.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        <ul>
          {orders.map((order) => (
            <li key={order._id}>
              <strong>Order ID:</strong> {order._id} <br />
              <strong>Store:</strong> {order.store.name} <br />
              <strong>Status:</strong>
              <select
                value={order.status}
                onChange={(e) => handleStatusChange(order._id, e.target.value)}
              >
                <option value="En attente">En attente</option>
                <option value="En cours">En cours</option>
                <option value="Livrée">Livrée</option>
                <option value="Annulée">Annulée</option>
              </select>
              <br />
              <strong>Items:</strong>
              <ul>
                {order.items.map((orderItem) => (
                  <li key={orderItem.item._id}>
                    {orderItem.item.name} (Qty: {orderItem.quantity})
                  </li>
                ))}
              </ul>
              <button onClick={() => onDelete(order._id)}>Delete Order</button>
              <hr />
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default OrderList;
