import React, { useState } from 'react';
import axios from 'axios';

const StoreForm = ({ onStoreAdded }) => {
  const [name, setName] = useState('');
  const [address, setAddress] = useState('');

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      const config = {
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': localStorage.getItem('token'), // Get token from localStorage
        },
      };
      const body = JSON.stringify({ name, address });
      const res = await axios.post('/api/stores', body, config);
      console.log(res.data);
      onStoreAdded(); // Callback to refresh store list
      setName('');
      setAddress('');
    } catch (err) {
      console.error(err.response.data);
    }
  };

  return (
    <form onSubmit={onSubmit}>
      <h2>Add New Store</h2>
      <div>
        <input
          type="text"
          placeholder="Store Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
      </div>
      <div>
        <input
          type="text"
          placeholder="Address (Optional)"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
        />
      </div>
      <button type="submit">Add Store</button>
    </form>
  );
};

export default StoreForm;
