import React, { useEffect, useState } from 'react';
import axios from 'axios';

const StoreList = ({ refresh }) => {
  const [stores, setStores] = useState([]);

  useEffect(() => {
    const fetchStores = async () => {
      try {
        const config = {
          headers: {
            'x-auth-token': localStorage.getItem('token'),
          },
        };
        const res = await axios.get('/api/stores', config);
        setStores(res.data);
      } catch (err) {
        console.error(err.response.data);
      }
    };
    fetchStores();
  }, [refresh]);

  const onDelete = async (id) => {
    try {
      const config = {
        headers: {
          'x-auth-token': localStorage.getItem('token'),
        },
      };
      await axios.delete(`/api/stores/${id}`, config);
      setStores(stores.filter(store => store._id !== id));
    } catch (err) {
      console.error(err.response.data);
    }
  };

  return (
    <div>
      <h2>Your Stores</h2>
      {stores.length === 0 ? (
        <p>No stores found. Add a new store above!</p>
      ) : (
        <ul>
          {stores.map((store) => (
            <li key={store._id}>
              {store.name} {store.address && `(${store.address})`}
              <button onClick={() => onDelete(store._id)}>Delete</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default StoreList;
